    // isFull checks durum and girdi TextField area
    private boolean isFull(){
        if(stateTF.getText().equals("") || inputTF.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Table Has Empty Space");
            return false;
        }return true;
    }
    
    
    private void mooreMacButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mooreMacButtonActionPerformed
        
        if(isFull()){
            String[] st = stateTF.getText().split(",");
            String[] inp = inputTF.getText().split(",");
            
            StateTable stable = new StateTable(st, inp);
            this.setVisible(false);
            stable.setVisible(true);
        }                                       
    }//GEN-LAST:event_mooreMacButtonActionPerformed

